import React, { Component } from 'react';
import './Item.css';

export default class Item extends Component {

  handleCheck(key) {
    console.log(key);
  }

  render() {
    const { title, key } = this.props;

    return (
      <li className="todo-list__list-item__item">
        <h6>{title}</h6>
        <input type="checkbox" onChange={() => this.handleCheck(key)} />
      </li>
    )
  }
}

