import React, { Component } from 'react';
import './TodoList.css';
import SearchBox from './SearchBox/SearchBox';
import Toolbar from './Toolbar/Toolbar';
import Item from './Item/Item';

export default class TodoList extends Component {
  constructor(props) {
    super(props);

    this.state = {
      selectedItems: []
    }
  }

  selectItem(item) {
    const { selectedItems } = this.state;


  }

  render() {

    /*
      BEM (Block - Element - Modifier)
    */

    const todos = [
      { id: 1, title: 'Do something', done: false },
      { id: 2, title: 'Do something', done: false },
      { id: 3, title: 'Do something', done: true },
      { id: 4, title: 'Do something', done: false },
    ];

    return (
      <div className="todo-list">
        <h1 className="todo-list__title --main">TODO LIST</h1>
        <h6 className="todo-list__title --sub">Notice your work</h6>

        <SearchBox />
        <Toolbar />

        <ul className="todo-list__list-item">
          {todos.map(item => <Item key={item.id} id={item.id} title={item.title} checked={item.done} />)}
        </ul>

      </div>
    )
  }
}
